import React, { Component } from 'react';
import './App.css';
import Game from './Game.js';
import Score from './Score.js';

class App extends Component {
  constructor(props){
    super(props);
    this.handleAnswer = this.handleAnswer.bind(this);
  }
     state = {
     correctAnswer: 0,
     numQuestions: 0,
   };

   handleAnswer = answerWasCorrect => 
    {
     if (answerWasCorrect) {
       this.setState(currState => ({
         correctAnswer: currState.correctAnswer + 1,
       }));
   }
     this.setState(currState => ({
       numQuestions: currState.numQuestions + 1,
     }));
    };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <a
            className="App-link"
            href="localhost:3000"
            target="_blank"
            rel="noopener noreferrer"
          >
            JIKRU
          </a>
        </header>
        <div className="game">
          <h2>Mental Math</h2>
          <Game handleAnswer={this.handleAnswer} />
	        <Score numCorrect={this.state.correctAnswer} numQuestions={this.state.numQuestions} />
        </div>
        <div className="game">
          <h2>Why Jikru...</h2>
            Daily 15 min calculation practice  - 
              <ul>
                <ol>Improves brain's thinking capability</ol>
                <ol>Improves problem solving skills</ol>
            </ul>
          </div>
      </div>
    );
  }
}

export default App;
